import React, { Component } from 'react'
import axios from 'axios'
import { Header, Card, Segment, TextArea, ButtonGroup, Button,Form } from 'semantic-ui-react'
import Cookies from 'universal-cookie'

const cookie = new Cookies()
const damageUrl = 'http://localhost:8080/sigemadi/api/damages'

//PODE SER CONVERTIDO EM FUNÇÃO EM VEZ DE CLASS
class Report_Material extends Component {

    constructor (props) {
        super(props)
        this.onConfirm = this.onConfirm.bind(this)
        this.onCancel = this.onCancel.bind(this)
    }
    state = {
        description:''
    }

    style = {
        display: 'block',
        margin: 10,
        marginBottom: 30
    }

    onConfirm() {
        //fazer o pedido de report de avaria

        if(this.state.description.length>0){
            const userId = cookie.get('userinfo').id
            const materialId = this.props.match.params['id']
            axios.post(damageUrl,{"material":materialId,"description":this.state.description,"user":userId})
            .then(resp=> {
                console.log(resp.status)
                const url = this.props.location.pathname.replace('/report','/material')
                this.props.history.push(url)
            })
            .catch(err => console.log(err))

        }
        else{
            alert('Por favor insira uma descrição da avaria')
        }
    }

    onCancel() {
        this.props.history.goBack()
    }

    render() {
        return (
            <div>
                <Header size='medium'>Reportar Avaria: </Header>
                <hr />
                <Card fluid>
                    <Card.Content>
                        <Card.Header>ATB 1</Card.Header>
                        <Card.Meta>{
                            this.props.match.params['id']
                        }
                        </Card.Meta>
                        <Card.Description>
                            <Header attached='top' color='red'>
                                Descrição da Avaria
                            </Header>
                            <Segment>
                                <Form>
                                    <TextArea required onChange={(event,object)=> this.setState({description:object.value})} rows='2' maxLength='200'></TextArea>
                                </Form>
                            </Segment>
                        </Card.Description>
                    </Card.Content>
                    <ButtonGroup fluid>
                        <Button basic color='green' onClick={this.onConfirm} content='Confirmar' />
                        <Button basic color='red' onClick={this.onCancel} content='Cancelar'/>
                    </ButtonGroup>
                    <Card.Content extra>
                    </Card.Content>
                </Card>
            </div >
        )
    }
}

export default Report_Material
