import React, { Component } from 'react'
import { Label, Button, Header, List, Card, Form, CardGroup, Divider } from 'semantic-ui-react'
import { damageUrl, damagesStatesUrl } from '../Links'
import axios from 'axios'
import { SemanticToastContainer } from 'react-semantic-toasts';
import Response_Handler from '../Response_Handler'

const titles = [
    {
        title: 'ID do Material',
        value: 'material'
    },
    {
        title: 'ID do Pedido',
        value: 'request'
    },
    {
        title: 'Utilizador',
        value: 'user'
    },
    {
        title: 'Data da Avaria',
        value: 'start_date'
    },
    {
        title: 'Data de Resolução',
        value: 'close_date'
    }
]

const Solução = 'O led não funcionava devido a um circuito queimado'

//PODE SER CONVERTIDO EM FUNÇÃO EM VEZ DE CLASS
class History_Material_Details extends Component {

    constructor (props) {
        super(props)
        this.buildInfoTable = this.buildInfoTable.bind(this)
    }

    state = {
        id: '',
        material: '',
        description: '',
        start_date: '',
        report: '',
        close_date: '',
        state: '',
        request: null,
        user: ''
    }

//alterei para didmount

    componentDidMount() {
        const id = this.props.match.params['id']
        axios.get(damageUrl.replace(':id', id))
            .then(resp => {
                this.setState({ ...resp.data })
            }).catch(err => Response_Handler(err.response))
    }

    buildInfoTable() {
        return titles.map(t => {
            if (this.state[t.value] === null) return null
            return (
                <div className="item" key={t.title}>
                    <div className="content">
                        <div className="left floated header">{t.title}</div>
                    </div>
                    <div className="right floated description" >{this.state[t.value]}</div>
                </div>
            )
        })
    }

    buildStateInfo() {

        //FAZER VERIFICAÇÕES E MANDAR ALERTS EM TUDO O QUE POSSA NAO SER BEM FEITO, VER OS ERROS RETORNADOS PELA API
        return this.state.state === 'resolvido' ?
            <Label size='huge' color='green'>
                Estado:
            <Label.Detail>Arranjado</Label.Detail>
            </Label>
            :
            <Label size='huge' color='red'>
                Estado:
             <Label.Detail>Avariado</Label.Detail>
            </Label>
    }

    render() {
        return (
            <div>
                <SemanticToastContainer />
                <Header size='medium'>Detalhes do Material</Header>
                {
                    this.buildStateInfo(this)

                }
                <List divided>
                    {
                        this.buildInfoTable()
                    }
                </List>
                <CardGroup>
                    <Card fluid>
                        <Card.Content>
                            <Card.Header>Descrição da Avaria</Card.Header>
                            <Card.Description>
                                <Divider />
                                <Card.Header size='small'>{this.state.description}</Card.Header>
                            </Card.Description>
                        </Card.Content>
                    </Card>
                    <Card fluid>
                        <Card.Content>
                            <Card.Header>Descrição da Solução</Card.Header>
                            <Card.Description>
                                <Divider />
                                <Card.Header size='small'>{this.state.report}</Card.Header>
                            </Card.Description>
                        </Card.Content>
                    </Card>
                </CardGroup>
            </div>
        )
    }
}
export default History_Material_Details
