import React, { Component } from 'react'
import { Link } from 'react-router-dom'



//PODE SER CONVERTIDO EM FUNÇÃO EM VEZ DE CLASS
class Courses_Managment extends Component {

    constructor (props) {
        super(props)

        this.deleteCourse = this.deleteCourse.bind(this)
        this.addCourse = this.addCourse.bind(this)
    }

    state = {
        courses: ['Eletrónica', 'Laboratório de Informática e de Computadores', 'Arquitetura de Computadores']
    }

    deleteCourse(name) {
        let stateCourses = this.state.courses
        let newCourses = stateCourses.reduce((courses, course) => {
            if (course != name) {
                courses.push(course)
            }
            return courses
        }, [])

        //fazer pedido de delete
        this.setState({ courses: [...newCourses] })
    }

    addCourse() {
        const course = document.getElementById("addCourse").value
        document.getElementById("addCourse").value = ""
        let courses = this.state.courses
        courses.push(course)
        this.setState({ courses: [...courses] })
    }

    render() {
        return (
            <div>
                <h2>Área Cientifica: {this.props.match.params['id']}</h2>
                <hr />
                <h2>Unidades Curriculares</h2>
                <div className="ui medium action input">
                    <input id="addCourse" type="text" placeholder="Unidade curricular..." />
                    <button className="ui button" onClick={this.addCourse}>Adicionar</button>
                </div>
                <hr />
                <div className="ui big middle aligned divided list">
                    {
                        this.state.courses.map(course => {
                            return <div className="item" key={course}>
                                <div className="right floated content">
                                    <button className="big ui compact icon button" onClick={() => this.deleteCourse(course)}><i className="red close icon"></i></button>
                                </div>
                                <div className="content">
                                    {course}
                                </div>
                            </div>

                        })

                    }
                </div>
            </div>
        )
    }
}
export default Courses_Managment
