import React, { Component } from 'react'
import { Link } from 'react-router-dom'



//PODE SER CONVERTIDO EM FUNÇÃO EM VEZ DE CLASS
class Cientific_Areas_Managment extends Component {

    constructor (props) {
        super(props)

        this.deleteArea = this.deleteArea.bind(this)
        this.addArea = this.addArea.bind(this)
    }

    state = {
        areaCientificas: ['Eletrónica', 'Informática', 'Quimica', 'Sinais']
    }

    deleteArea(name) {
        let stateAreas = this.state.areaCientificas
        let newAreas = stateAreas.reduce((areas, area) => {
            if (area != name) {
                areas.push(area)
            }
            return areas
        }, [])

        //fazer pedido de delete
        this.setState({ areaCientificas: [...newAreas] })
    }

    addArea() {
        const area = document.getElementById("addArea").value
        let areas = this.state.areaCientificas
        areas.push(area)
        this.setState({ areaCientificas: [...areas] })
    }

    render() {
        return (
            <div>
                <h3 className="ui middle aligned medium header">Áreas Científicas</h3>
                <div className="ui action input">
                    <input id="addArea" type="text" placeholder="Área cientifica..." />
                    <button className="ui button" onClick={this.addArea}>Adicionar</button>
                </div>
                <hr />
                <div className="ui big middle aligned divided list">

                    {
                        this.state.areaCientificas.map(area => {
                            const url = `/auth/admin/areas/${area}/course`
                            return <div className="item" key={area}>
                                <div className="right floated content">
                                    <button className="big ui compact icon button" onClick={() => this.deleteArea(area)}><i className="red close icon"></i></button>
                                </div>
                                <div className="content">
                                    <Link to={url}> {area}</Link>
                                </div>
                            </div>

                        })

                    }
                </div>
            </div>
        )
    }
}
export default Cientific_Areas_Managment
