import React, { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import { Header, Button, List, Divider } from 'semantic-ui-react'
import axios from 'axios'
import { materialsUrl } from '../Links'
import Response_Handler from '../Response_Handler'
import Filter_Material from '../Filter_Material'
import { SemanticToastContainer } from 'react-semantic-toasts';

//PODE SER CONVERTIDO EM FUNÇÃO EM VEZ DE CLASS
function Material_Management(props) {

    const [materials, setMaterials] = useState([])


    function setFilters(search) {
        axios.get(materialsUrl + (search || ''))
            .then(resp => {
                setMaterials(resp.data['materials'])
            })
            .catch(err => Response_Handler(err.response))
    }

    function onSubmit() {
        props.history.push('/auth/labmanager/addmaterial')
    }

    function deleteMaterial(id) {
        const button = document.getElementById('delete' + id)
        button.disabled = true

        axios.delete(materialsUrl + `/${id}`)
            .then(resp => {
                Response_Handler(resp)
                setTimeout(() =>setFilters(props.location.search), 2000)

            })
            .catch(err => {
                Response_Handler(err.response)
                button.disabled = false
            })


        //SE FIZER A FILTRAGEM E APAGAR UM ELE NAO SE REFLETE AI SO SE FIZER RESET NO FILTRO PQ EU ESTOU A AFETAR MATERIAL E NAO FILTERED
        //MAS COMO ISTO VAI TER PEDIDOS NAO HA PROBLEMA PQ VOU BUSCAR OUTRA VEZ COM ESSE FILTRO E DEPOIS JA NAO APARECE O MATERIAL

        //fazer pedido de delete
        //setState({ material: [...materials] })
    }
    function createMaterialList() {
        let id = ''
        let num = 0
        return materials.map(material => {

            if (material.id != id) {
                id = material.id
                num = -1
            }
            num++
            return <List.Item key={material.id + num}>
                <List.Content floated='right'>
                    <Button size='big' id={'delete' + material.id} compact onClick={() => deleteMaterial(material.id)} icon='red close'></Button>
                </List.Content>
                <List.Content>
                    <Link to={props.location.pathname + '/' + material.id}>
                        {material.id}
                    </Link>
                </List.Content>
            </List.Item>


        })
    }

    return (
        <div>
            <SemanticToastContainer />
            <Header size='medium'>Material:</Header>

            <Filter_Material {...props} setFilters={setFilters}></Filter_Material>
            <div>
                <Button icon='check circle' content='Adicionar Material' onClick={onSubmit} ></Button>
            </div>
            <Divider />
            <List divided>
                {
                    createMaterialList()
                }
            </List>
        </div>
    )
}

export default Material_Management
