import React, { Component } from 'react'
import { Header, List, Button, Input } from 'semantic-ui-react'
imp

const subjectsUrl = 'http://localhost:8080/sigemadi/api/scientificareas/:id/subjects'
//PODE SER CONVERTIDO EM FUNÇÃO EM VEZ DE CLASS
class Courses_Management extends Component {

    constructor (props) {
        super(props)

        this.deleteCourse = this.deleteCourse.bind(this)
        this.addCourse = this.addCourse.bind(this)
    }

    
    //FALTA FAZER REFRESH QUANDO ADICIONO OU ELIMINO 

    state = {
        courses: ['Eletrónica', 'Laboratório de Informática e de Computadores', 'Arquitetura de Computadores']
    }

    componentWillMount(){
        const id = this.props.match.params['id']

    }

    deleteCourse(name) {
        let stateCourses = this.state.courses
        let newCourses = stateCourses.reduce((courses, course) => {
            if (course != name) {
                courses.push(course)
            }
            return courses
        }, [])

        //fazer pedido de delete
        this.setState({ courses: [...newCourses] })
    }

    addCourse() {
        const course = document.getElementById("addCourse").value
        document.getElementById("addCourse").value = ""
        let courses = this.state.courses
        courses.push(course)
        this.setState({ courses: [...courses] })
    }

    render() {
        return (
            <div>
            <Header size='medium'>Área Cientifica: {this.props.match.params['id']}</Header>
                <hr />
                <Header size='medium'>Unidades Curriculares:</Header>
                <div className="ui medium action input">
                    <Input id="addCourse" type="text" action={<Button onClick={this.addCourse}>Adicionar</Button>} placeholder="Unidade curricular..." />
                </div>
                <List divided>
                    {
                        this.state.courses.map(course => {
                            return <List.Item key={course}>
                                <List.Content floated='right'>
                                    <Button size='big' compact onClick={() => this.deleteCourse(course)} icon='red close'></Button>
                                </List.Content>
                                <List.Content>
                                    {course}
                                </List.Content>
                            </List.Item>
                        })

                    }
                </List>
            </div>
        )
    }
}
export default Courses_Management
